import User from "./User";
import { useGetUsersQuery } from "./usersApiSlice";

const UsersList = () => {
  const {
    data: users,
    isLoading,
    isSuccess,
    isError,
    error,
  } = useGetUsersQuery("usersList", {
    /*These added fields refetch data so 
    the user is always looking at fresh data
    if any changes are made on another device  
    pollingInterval: 60000 = every 60 seconds
    refetchOnfocus = when focus is on another window and come back to the app,
    refetchOnMountOrArgChange = fetching data on re mount */
    pollingInterval: 60000,
    refetchOnfocus: true,
    refetchOnMountOrArgChange: true,
  });

  let content;

  // Loading state
  if (isLoading) content = <p>Loading...</p>;

  // Error state
  if (isError) {
    content = (
      <p className={isError ? "errmsg" : "offscreen"}>{error?.data.message}</p>
    );
  }

  // Success state
  if (isSuccess) {
    // using data:users that was renamed in useGetUserQuery
    const { ids } = users;

    const tableContent =
      ids?.length && ids.map((userId) => <User key={userId} userId={userId} />);

    content = (
      <table className="table table--users">
        <thead className="table__thead">
          <tr>
            <th scope="col" className="table__th user__username">
              Username
            </th>
            <th scope="col" className="table__th user__roles">
              Roles
            </th>
            <th scope="col" className="table__th user__edit">
              Edit
            </th>
          </tr>
        </thead>
        <tbody>{tableContent}</tbody>
      </table>
    );
  }

  return content;
};

export default UsersList;
