import React from "react";
import { useGetNotesQuery } from "./notesApiSlice";

const NotesList = () => {
  const {
    data: notes,
    isLoading,
    isSuccess,
    isError,
    error,
  } = useGetNotesQuery();

  let content;

  // Loading state
  if (isLoading) content = <p>Loading...</p>;

  // Error state
  if (isError) {
    content = <p className="errmsg">{error?.data.message}</p>;
  }

  // Success state
  if (isSuccess) {
    // using data:notess that was renamed in useGetNotesQuery
    const { ids } = notess;

    const tableContent = ids?.length
      ? ids.map((notesId) => <Notes key={notesId} notesId={notesId} />)
      : null;

    content = (
      <table className="table table--notess">
        <thead className="table__thead">
          <tr>
            <th scope="col" className="table__th notes__notesname">
              Notesname
            </th>
            <th scope="col" className="table__th notes__roles">
              Roles
            </th>
            <th scope="col" className="table__th notes__edit">
              Edit
            </th>
          </tr>
        </thead>
        <tbody>{tableContent}</tbody>
      </table>
    );
  }

  return content;
};

export default NotesList;
